import { useEffect, useRef, useState } from "react"

export const useFetch = ( url ) => {
    const [state, setState] = useState({
        data: null,
        error: null,
        loading: true
    });

    const isMounted = useRef(true);

    useEffect(() => {
        return () => {
            isMounted.current = false;
        }
    }, [])

    useEffect(() => {
        
        setState(
            {
                data: null,
                error: null,
                loading: true
            }
        );

        fetch( url )
            .then( resp => resp.json()) 
            .then( data => {

                setTimeout(() => {

                    if (isMounted){
                        setState({
                            data: data,
                            error: null,
                            loading: false,
                        });
                    }else{
                        console.log("Unounted Component")
                    }
                }, 4000);

            }).catch(error => {
                setState({
                    data: null,
                    error: error,
                    loading: false,
                });
            });

    }, [url]);

    return state;
}
