import React, { Fragment, useState } from 'react';
import './counter.css';

export const CounterApp = () => {
    const [counter, setCounter] = useState({
        counter1: 10,
        counter2: 20
    });

    const handleAdd = () => {
        setCounter(counter + 1);
    }

    return (
        <Fragment>
            <h1>
                Counter
            </h1>
            <hr />
            <button 
                className="btn btn-primary"
                onClick={ handleAdd }
            >
                +
            </button>
        </Fragment>
    )
}
