import React, { useState } from 'react';
import { useForm } from '../../Hooks/useForm';
import './effects.css';

export const Form = () => {

    const user = {
        name: '',
        email: '',
        password: ''
    };
    const state = useForm();

    const { name, email, password } = formState;

    

    return (
        <div>
            <h1>
                SimpleForm useEffect
            </h1>
            <div className="row">
                <form className="mt-5">
                    <div className="mb-3">
                        <input
                            type="text"
                            name="name"
                            maxLength="30"
                            value={ name }
                            autoComplete="off"
                            className="form-control"
                            placeholder="Type your name"
                            onChange={ handleInputChange }
                        />
                    </div>
                    <div className="mb-3">
                        <input
                            type="email"
                            name="email"
                            maxLength="50"
                            value={ email }
                            autoComplete="off"
                            className="form-control"
                            placeholder="Type your email"
                            onChange={ handleInputChange }
                        />
                    </div>
                    <div className="mb-3">
                        <input
                            type="password"
                            name="password"
                            value={ password }
                            autoComplete="off"
                            className="form-control"
                            placeholder="*****"
                            onChange={ handleInputChange }
                        />
                    </div>
                </form>
            </div>
        </div>
    )
}
