import { useEffect, useState } from "react"

export const useFetch = ( url ) => {
    const [state, setState] = useState({
        data: null,
        loading: true,
        error: null
    });

    useEffect(() => {
        
        fetch( url )
            .then( resp => resp.json()) 
            .then( data => {

                setState({
                    data: data,
                    error: null,
                    loading: false,
                });

            }).catch(error => {
                setState({
                    data: null,
                    error: error,
                    loading: false,
                });
            });

    }, [url]);

    return state;
}
