import { useState } from 'react';

export const useCounter = ( initialValue = 10 ) => {
    const [state, setState] = useState(initialState)

    const increment = () => {
        setState( state + 1 );
    }

    const decrement = () => {
        setState( state - 1);
    }

    return {
        state,
        decrement,
        increment
    };
}
