import React, { Fragment, useState } from 'react';
import './counter.css';

export const CounterApp = () => {
    const [{ counter1, counter2 }, setCounter] = useState({
        counter1: 10,
        counter2: 20
    });

    const handleAdd = () => {
        setCounter(c => {
            c.counter1 = counter1 + 1;
        });
    }

    return (
        <Fragment>
            <h1>
                Counters
            </h1>
            <hr />
            <div className="row">
                <h4>
                    {counter1}
                </h4>
                <h4>
                    {counter2}
                </h4>
            </div>
            <button 
                className="btn btn-primary"
                onClick={ handleAdd }
            >
                +
            </button>
        </Fragment>
    )
}
